mapper.update:
update luggage

    statue = #{statue},
    location = #{location}

where luggage_id = #{luggageId}

mapper.select:
            select * from luggage
            

                
                      and luggage_id = #{luggageId}
                
                
                    and flight_id = #{flightId}
                
                
                    and tourist_id = #{touristId}
                

            
            order by tourist_id desc
